import React, { useEffect, useState } from "react";
import { LinkContainer } from "react-router-bootstrap";
import axios from "axios";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";

const DetailsPage = ({ history, match }) => {
  const pokemonID = match.params.id;

  const [pokemonList, setPokemonList] = useState([]);
  const [pokemon, setPokemon] = useState({});
  const [loading, setLoading] = useState(false);

  const fetchPokemon = async () => {
    setLoading(true);
    const res = await axios.get(
      "https://raw.githubusercontent.com/Biuni/PokemonGO-Pokedex/master/pokedex.json"
    );

    setPokemonList(res.data.pokemon);
    setLoading(false);
  };

  useEffect(() => {
    if (!pokemonList.length) {
      fetchPokemon();
    } else if (!loading && pokemonList.length) {
      const pokemonItem = pokemonList.filter(
        (item) => item.id.toString() === pokemonID.toString()
      );
      setPokemon(pokemonItem[0]);
    }
    console.log(pokemon);
  }, [pokemonID, pokemonList, loading]);

  return loading ? (
    <p>Loading...</p>
  ) : (!loading && pokemon !== {}) || (!loading && pokemon !== undefined) ? (
    <>
      <LinkContainer to="/">
        <Button>Go Back</Button>
      </LinkContainer>
      <Row>
        <Col>
          <h3>
            {pokemon.num} - {pokemon.name}
          </h3>
        </Col>

        <Col>
          <Image rounded src={pokemon.img} />
        </Col>
      </Row>
      <Table responsive>
        <thead>
          <tr>
            <th>Type</th>
            <th>Weaknesses</th>
            <th>Height</th>
            <th>Weight</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{pokemon.type.join(", ")}</td>
            <td>{pokemon.weaknesses.join(", ")}</td>
            <td>{pokemon.height}</td>
            <td>{pokemon.weight}</td>
          </tr>
        </tbody>
      </Table>
    </>
  ) : (
    <p>No pokemon found...</p>
  );
};

export default DetailsPage;
