import React, { useEffect, useState } from "react";
import axios from "axios";

const DetailsPage = ({ history, match }) => {
  const pokemonID = match.params.id;

  const [pokemonList, setPokemonList] = useState([]);
  const [pokemon, setPokemon] = useState({});

  const fetchPokemon = async () => {
    const res = await axios.get(
      "https://raw.githubusercontent.com/Biuni/PokemonGO-Pokedex/master/pokedex.json"
    );

    setPokemonList(res.data.pokemon);
  };

  useEffect(() => {
    if (!pokemonList.length) {
      fetchPokemon();
    }

    const pokemonItem = pokemonList.filter(
      (item) => item.id.toString() === pokemonID.toString()
    );
    setPokemon(pokemonItem[0]);
  }, [pokemonID, pokemonList]);

  return pokemon !== {} || pokemon !== undefined ? (
    <p>No Pokemon Found...</p>
  ) : (
    <p>{pokemon.name}</p>
  );
};

export default DetailsPage;
