  //       </Row>
  //       <Table responsive>
  //         <thead>
  //           <tr>
  //             <th>Type</th>
  //             <th>Weaknesses</th>
  //             <th>Height</th>
  //             <th>Weight</th>
  //           </tr>
  //         </thead>
  //         <tbody>
  //           <tr>
  //             <td>{pokemon.type.join(", ")}</td>
  //             <td>{pokemon.weaknesses.join(", ")}</td>
  //             <td>{pokemon.height}</td>
