import React, { useEffect, useState } from "react";
import { LinkContainer } from "react-router-bootstrap";
import axios from "axios";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";

const DetailsPage = ({ history, match }) => {
  const pokemonID = match.params.id;

  const [pokemonList, setPokemonList] = useState([]);
  const [pokemon, setPokemon] = useState({});

  const fetchPokemon = async () => {
    const res = await axios.get(
      "https://raw.githubusercontent.com/Biuni/PokemonGO-Pokedex/master/pokedex.json"
    );

    setPokemonList(res.data.pokemon);
  };

  useEffect(() => {
    fetchPokemon();

    const pokemonItem = pokemonList.filter(
      (item) => item.id.toString() === pokemonID.toString()
    );
    setPokemon(pokemonItem[0]);
  }, [pokemonID, pokemonList]);

  return pokemon === {} || pokemon === undefined ? (
    <>
      <LinkContainer to="/">
        <Button>Go Back</Button>
      </LinkContainer>
      <p>No Pokemon Found...</p>
    </>
  ) : (
    <>
      <LinkContainer to="/">
        <Button>Go Back</Button>
      </LinkContainer>
      <Row>
        <Col>
          <h3>
            {pokemon.num} - {pokemon.name}
          </h3>
        </Col>

        <Col>
          <Image rounded src={pokemon.img} />
        </Col>
      </Row>
      <Table responsive>
        <thead>
          <tr>
            <th>Type</th>
            <th>Weaknesses</th>
            <th>Height</th>
            <th>Weight</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{pokemon.type.join(", ")}</td>
            <td>{pokemon.weaknesses.join(", ")}</td>
            <td>{pokemon.height}</td>
            <td>{pokemon.weight}</td>
          </tr>
        </tbody>
      </Table>
    </>
  );
};

export default DetailsPage;
